const lazyLoad = service => (options) => {
  // 设置各服务的 api host
  const settings = {
    api: `https://${service}.api.qcloud.com/v2/index.php`,
    // 预留
    actions: {}
  };
  // 重要事情说三遍：
  // 核心代码开始
  // 核心代码开始
  // 核心代码开始
  return new Proxy({}, {  // 创建代理
    // 定义 get 方法
    // 例子：
    // const obj = new Obj();
    // obj.prop 获取属性，用的 get 方法
    // obj.prop() 获取方法，用的依然是 get 方法
    get: (target, property) => 
      (opts) => {
